// src/Home.jsx
import React, { useState, useEffect, useRef } from 'react';
import './home.css';
import addBtn from '../assets/add-30.png';
import msg from "../assets/message.svg";
import home from '../assets/home.svg';
import saved from '../assets/bookmark.svg';
import rocket from '../assets/rocket.svg';
import sendBtn from '../assets/send.svg';
import user from '../assets/user-icon.png';
import gptImg from '../assets/chatgptLogo.svg';
import gptLogo from '../assets/chatgpt.svg';

const responseMap = {
  "hi": "hello",
  "hello": "hi there!",
  "how are you?": "I'm good, thanks for asking!",
  "what's your name?": "I'm your assistant!",
  "bye": "Goodbye!",
};

function Home() {
  const [chatMessages, setChatMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const chatContainerRef = useRef(null);

  const sendMessage = () => {
    if (inputValue.trim() === "") return;

    const newMessage = { text: inputValue, type: 'outgoing', time: getCurrentTime() };
    setChatMessages(prevMessages => [...prevMessages, newMessage]);
    setInputValue('');

    // Check for a response in responseMap
    const response = responseMap[inputValue.toLowerCase()];

    // Send response or default message if not found
    setTimeout(() => {
      if (response) {
        addAutoReply(response);
      } else {
        addAutoReply("I'll need to look that up.");
      }
    }, 1000);
  };

  const addAutoReply = (response) => {
    const newMessage = { text: response, type: 'incoming', time: getCurrentTime() };
    setChatMessages(prevMessages => [...prevMessages, newMessage]);
  };

  const getCurrentTime = () => {
    const now = new Date();
    const hours = now.getHours();
    const minutes = now.getMinutes();
    return `${hours}:${minutes < 10 ? '0' : ''}${minutes}`;
  };

  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [chatMessages]);

  return (
    <div className="App">
      <div className="sideBar">
        <div className="upperSide">
          <div className="upperSideTop">
            <img src={gptLogo} alt="logo" className="logo" />
            <span className="brand">Chatgpt</span>
          </div>
          <button className="midBtn">
            <img alt="add" src={addBtn} className="addBtn" />
            New Chat
          </button>
          <div className="upperSideBottom">
            <button className="query">
              <img alt="query" src={msg} className="Query" /> What is programming
            </button>
            <button className="query">
              <img alt="query" src={msg} className="Query" /> What is programming
            </button>
          </div>
        </div>
        <div className="lowerSide">
          <div className="listItems">
            <img src={home} alt="" className="listitemsImg" />
            Home
          </div>
          <div className="listItems">
            <img src={saved} alt="" className="listitemsImg" />
            Saved
          </div>
          <div className="listItems">
            <img src={rocket} alt="" className="listitemsImg" />
            Upgrade
          </div>
        </div>
      </div>
      <div className="main">
        <div className="chats">
          {chatMessages.map((msg, index) => (
            <div key={index} className={`chat ${msg.type}`}>
              <img src={msg.type === 'outgoing' ? user : gptImg} alt="" className="chatImg" />
              <p className="txt">{msg.text}</p>
            </div>
          ))}
        </div>
        <div className="chatFooter">
          <div className="inp">
            <input type="text" classNamevalue={inputValue} onChange={e => setInputValue(e.target.value)} placeholder="Send a message..." />
            <button className="send" onClick={sendMessage}>
              <img src={sendBtn} alt="send" />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
